#importing paxckagesimport pandas as pdimport numpy as npimport matplotlib.pyplot as pltfrom sklearn.datasets import load_breast_cancercancer=load_breast_cancer()print(cancer.keys())print(cancer.target_names)data=pd.DataFrame(cancer["data"],columns=cancer["feature_names"])Target=pd.DataFrame(cancer.target)data=pd.concat([data,Target],axis="columns")y=data.iloc[:,-1]x=data.drop(0, axis="columns")from sklearn.preprocessing import StandardScalerscaler=StandardScaler()scaler.fit(x)new_x=scaler.transform(x)from sklearn.decomposition import PCApca=PCA(n_components=2)newx=pca.fit_transform(new_x)from sklearn.model_selection import train_test_splitX_train,X_test,y_train,y_test=train_test_split(newx,y,test_size=1/4,random_state=1)from sklearn.linear_model import LinearRegressionsimple=LinearRegression()simple.fit(X_train,y_train)#predict valuey_predict=simple.predict(X_test)print(simple.score(X_test, y_test))