import pandas as pdimport numpy as npimport matplotlib.pyplot as pltimport seaborn as snsdata=pd.read_csv("csv.csv")x=data.iloc[:,0:20]y=data.iloc[:,-1]from sklearn.feature_selection import SelectKBestfrom sklearn.feature_selection import chi2bestfeature=SelectKBest(score_func=chi2,k=10)fit=bestfeature.fit(x,y)dfscores=pd.DataFrame(fit.scores_)dfcolumns=pd.DataFrame(x.columns)featurescore=pd.concat([dfcolumns,dfscores],axis=1)featurescore.columns=["specs","score"] #naming columnsprint(featurescore.nlargest(10,"score"))#print top 10 featues to select#using feature importancefrom sklearn.ensemble import ExtraTreesClassifiermodel=ExtraTreesClassifier()model.fit(x,y)print(model.feature_importances_)#plot graph of feature importances for better visualizationfeat_importances = pd.Series(model.feature_importances_, index=x.columns)feat_importances.nlargest(10).plot(kind='barh')plt.show()#with heatmapimport seaborn as sns#get correlations of each features in datasetcorrmat = data.corr()top_corr_features = corrmat.indexplt.figure(figsize=(20,20))#plot heat mapg=sns.heatmap(data[top_corr_features].corr(),annot=True,cmap="RdYlGn")